            }
            
            
            $model = new SignupForm();
            
            $model->username = $userName;
            $model->password = $password;
            $model->email = $email;
            
            $user = $model->signup();
            
            return $this->render('signup', [
                'model' => $model,
            ]);

         } catch (Exception $e) {
             $errorMessage =  $e->getMessage();
             echo json_encode(['status' => false, 'message' =>$errorMessage]);
        }   
        
    }

}
